import React, { useState, useEffect } from "react";
import "./LoginPage.css";
import { Container, Typography, Button } from "@material-ui/core";
import { Alert } from "@material-ui/lab";
import { Redirect } from "react-router-dom";
import TextInput from "../components/TextInput";
import axios from "axios";
import Loading from "./Loading";
import { useGoogleReCaptcha } from "react-google-recaptcha-v3";

function UpdateProfile(props) {
	const [name, setName] = useState("");
	const [nameError, setNameError] = useState(false);
	const [phoneNumber, setPhoneNumber] = useState("");
	const [numberError, setNumberError] = useState(false);

	const [loading, setLoading] = useState(false);
	const [type, setType] = useState(props.match.params.type);

	const [redirect, setRedirect] = useState(false);
	const [error, setError] = useState(false);

	const { executeRecaptcha } = useGoogleReCaptcha();

	const handleNameChange = (event) => {
		setName(event.target.value);
	};

	const handlePhoneNumberChange = (event) => {
		setPhoneNumber(event.target.value);
	};

	const keyPress = (event) => {
		if (event.key === "Enter") {
			handleSubmit();
		}
	};

	const handleSubmit = async () => {
		// let token = localStorage.getItem("authToken");
		// let url = `https://quizzie-api.herokuapp.com/${type}/updateProfile`;

		// let captcha = await executeRecaptcha("update_profile");

		if (name.trim().length === 0) {
			setNameError(true);
		} else setNameError(false);
		console.log(phoneNumber.toString())
		if (phoneNumber.toString().trim().length !== 10) {
			setNumberError(true);
		} else setNumberError(false);
	};

	const getDetails = async () => {
		let token = localStorage.getItem("authToken");
		let url = `https://quizzie-api.herokuapp.com/${type}`;

		try {
			await axios
				.get(url, {
					headers: {
						"auth-token": token,
					},
				})
				.then((res) => {
					setName(res.data.result1.name);
					if (res.data.result1.mobileNumber !== undefined)
						setPhoneNumber(
							res.data.result1.mobileNumber.toString()
						);
					else setPhoneNumber("");
					setLoading(false);
				});
		} catch (error) {
			console.log(error);
			// setRedirect(true);
		}
	};

	useEffect(() => {
		getDetails();
	}, []);

	// useEffect(() => {
	// 	let token = localStorage.getItem("authToken");
	// 	if (token === null) {
	// 		setLoading(false);
	// 		// setRedirect(true);
	// 		return;
	// 	}
	// }, []);

	if (redirect) {
		return <Redirect to="/dashboard" />;
	}
	return loading ? (
		<Loading />
	) : (
		<Container className="login-page">
			<div className="login-form">
				<Typography variant="h3" color="primary" className="login-head">
					Update Profile
				</Typography>
				<br />
				{error === true ? (
					<Alert severity="error">
						There was some error! Please try again...
					</Alert>
				) : null}
				<form className="form">
					<TextInput
						error={nameError}
						helperText={nameError ? "Name cannot be empty" : null}
						id="name"
						label="Name"
						type="text"
						className="form-input"
						variant="outlined"
						value={name}
						onChange={handleNameChange}
						onKeyPress={keyPress}
					></TextInput>
					<br />
					<TextInput
						error={numberError}
						helperText={numberError ? "Invalid Phone Number" : null}
						id="phone-number"
						type="number"
						label="Phone Number"
						className="form-input"
						variant="outlined"
						value={phoneNumber}
						onChange={handlePhoneNumberChange}
						onKeyPress={keyPress}
					></TextInput>
				</form>
				<Button className="login-btn" onClick={handleSubmit}>
					Update
				</Button>
			</div>
		</Container>
	);
}

export default UpdateProfile;
