const Immutable = require('immutable');
const { fromJS } = Immutable;

const options = ['status', 'retailerName'];

const query = {
  pageSize: 2,
  status: 'Unpaid',
  retailerName: 'Collins',
};

const magic = fromJS(query);

const result = magic.map((v, k) => {
  console.log('k:', k);
  console.log('v:', v);
  return options.includes(k);
});
const filtered = magic.filter((v, k) => {
  console.log('k:', k);
  console.log('v:', v);
  return options.includes(k);
});


console.log('🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰🐰');
console.log('result:', result);
console.log('filtered:', filtered);
